on.input.defaults = function() {
  input.jsplumb.importDefaults(data);
  output( { jsplumb: input.jsplumb })
};

on.input.el = function() {
  input.jsplumb.recalculateOffsets(data);
  output( { jsplumb: input.jsplumb });
};

on.input.el = function() {
  input.jsplumb.remove(data);
  output( { jsplumb: input.jsplumb });
};

on.input.el = function() {
  input.jsplumb.removeAllEndpoints(data, input.recurse);
  output( { jsplumb: input.jsplumb })
};

on.input.el = function() {
  input.jsplumb.repaint(data);
  output( { jsplumb: input.jsplumb })
};

on.input.container = function() {
  input.jsplumb.setContainer(data);
  output( { jsplumb: input.jsplumb });
};
